n$count7=sizeof($view7);\r\n$view30 = views_get_view_result(\'Newest_dataset\');\r\n$count30=sizeof($view30);\r\necho\'<div class=\"content\"><img alt=\"Data Sets\" src=\"\'.$base_url.\'/sites/all/themes/cms/images/whatsnew-datasets.jpg\" style=\"width: 279px; height: 155px;\" title=\"Newest Datasets\" /><div class=\"block-data-whats-new\">Find out what datasets and tools have been added to OGPL catalogs.</div><ul class=\"lists new-dataset-margin\"><li><span class=\"number-count\"><b>\' .$count7.\'</b></span> new datasets/tools added in last 7 days(<a href=\"\'.$base_url.\'/new/7\" title=\"view list\" class=\"hyperlink\" style=\"font-weight:normal;\">view list</a>)</li><li><span class=\"number-count\"><b>\'.$count30.\'</b></span> new datasets/tools added in last 30 days(<a href=\"\'.$base_url.\'/new/30\" title=\"view list\" class=\"hyperlink\" style=\"font-weight:normal;\">view list</a>)</li></ul></div>\'?>','Front End Newest Dataset Image What\'s New Page',3),(9,'<h2 class=\"centered\">Newest Datasets</h2>','Front End Newest Dataset Header What\'s New Page',2),(10,'<div class=\"read-more-align\"><a href=\"whats-new\" class=\"hyperlink\">Read More </a></div><div class=\"lists\"><ul class=\"lists\"><li><a href=\"#\">Jackson doctor faces sentencing</a></li><li><a href=\"#\">Kenyan Court:Arrest Sudan\'s Bashir</a></li><li><a href=\"#\">U.N. warns of infighting Libya brigades </a></li><li><a href=\"#\">Egypt\'s historic vote</a></li><li><a href=\"#\">UAE pardons political activists</a></li></ul><p>&nbsp;</p></div>','Rss feeds html',2),(11,'<p></p><h3 class=\"font-h3\">OGPL\'s Latest News</h3><p>OGPL gets you the latest news and updates on OGPL activity here. Watch this space to keep yourself updated on the latest OGPL happenings and introductions.</p>','Front End What\'s New Content',2),(12,'<h2>OGPL’s Latest Updates</h2>','Front End What\'s New Page Title',2),(13,'<div class=\"content\"><img alt=\"New Features\" src=\"[token global site-url]sites/all/themes/cms/images/whatsnew-.jpg\" title=\"New Features\" /><div class=\"data\">Check out how Data.gov is making news. You\'ll also find stories about Open Government and learn how Data.gov is supporting its core principles of transparency, participation and collaboration.<div class=\"btnsPan \"><a class=\"buttons green\" href=\"datasetcatalog\">Read More </a></div></div></div><p>&nbsp;</p>','whatsnew_news',2),(20,'<h2>OGPL Offerings</h2><!--learn --><div class=\"galleryBlocks alpha\"><h3>Learn</h3><img alt=\"Learn\" src=\"/drupal_svn/sites/all/themes/nic/images/img-learn.png\" title=\"Learn\" /><p>Are you a teacher, professor, or student? Young people across America are learning about Data.gov and we want you to be part of getting data into the classroom.</p></div><!--learn --><!--developers corner --><div class=\"galleryBlocks\"><h3>Developers Corner</h3><img alt=\"Developers Corner\" src=\"/drupal_svn/sites/all/themes/nic/images/img-developers.png\" title=\"Developers Corner\" /><p>Are you interested in sharing your mashups, apps, and ideas? Do you want to learn how to create app and mashups with some of the data hosted here on Data.gov</p></div><!--developers corner --><!--linked data --><div class=\"galleryBlocks\"><h3>Linked Data</h3><img alt=\"Linked Data\" src=\"/drupal_svn/sites/all/themes/nic/images/img-linked-data.png\" title=\"Linked Data\" /><p>Linked data is data in which real-world things are given addresses on the web (URIs), and data is published about them in machine-readable formats at those locations.</p></div><!--linked data -->','Front End Gallery Content Home Page',2),(21,'<h1>OGPL Offerings</h1>','Front End Gallery Header Home Page',2),(23,'','Front End Welcome Header Home Page',2),(25,'<div class=\"heading\">Catalog Filters<div></div></div>','Filters Header',2),(26,'Popular_Apps_Catalog','embed Popular_Apps_Catalog',4),(27,'Popular_Dataset_Catalog','embed Popular_Dataset_Catalog',4),(28,'Popular_Document_Catalog','embed Popular_Document_Catalog',4),(29,'Recent_Apps_Catalog','embed Recent_Apps_Catalog',4),(30,'Recent_Dataset_Catalog','embed Recent_Dataset_Catalog',4),(31,'Recent_Document_Catalog','embed Recent_Document_Catalog',4),(32,'<p>Test Data</p>','Static content MajorEvents',1),(33,'<div class=\"goi\"><a href=\"http://www.india.gov.in\" class=\"country-flag\" target=\"_blank\"><img alt=\"Country Flag\" src=\"/dms/sites/all/themes/nic/images/flag.png\" /></a><div class=\"gov\">GOVERNMENT OF INDIA</div><span class=\"ext\"></span></div>','Header: Country Flag Block',4),(34,'<div class=\"dataset-search-options\"><p style=\"padding-left: 10px; margin: 0;\" id=\"comment_text_0\" class=\"bz_comment_text\">Looking for datasets/Apps specific search? Please use the respective search options</p><ul><li><a target=\"_self\" title=\"Raw Data\" href=\"[token global site-url]search/apachesolr_search/?filters=type%3Adataset%20ss_cck_field_ds_catlog_type%3Acatalog_type_raw_data\">Raw data</a></li><li><a href=\"[token global site-url]search/apachesolr_search/?filters=type%3Adataset%20ss_cck_field_ds_catlog_type%3Acatalog_type_document\" title=\"Documents\">Documents</a></li><li><a href=\"[token global site-url]search/apachesolr_search/?filters=type%3Adataset%20ss_cck_field_ds_catlog_type%3Acatalog_type_data_apps\" title=\"Apps\">Apps</a></li><li><a target=\"_blank\" title=\"Tools\" href=\"[token global site-url]search/apachesolr_search/?filters=type%3Adataset%20ss_cck_field_ds_catlog_type%3Acatalog_type_data_tools\">Tools</a></li><li><a target=\"_blank\" title=\"Service\" href=\"[token global site-url]search/apachesolr_search/?filters=type%3Adataset%20ss_cck_field_ds_catlog_type%3Acatalog_type_data_service\">Services</a></li></ul></div>','Dataset Search Options',2),(35,'<?php\r\n$view = views_get_view_result(\'list_of_expired_contents\');\r\n$count=sizeof($view);\r\nif($count>1)\r\necho\'There are \'.$count .\' published contents due for review.Click <a href=\"expiredcontents\">here</a> for more details.\';\r\nif($count==1)\r\necho\'There is only \'.$count .\' published content due for review.Click <a href=\"expiredcontents\">here</a> for more details.\';\r\n?>','Block to display messages if content is due for review',3),(37,'<?php\r\nprint \'Below is a summary and listing of all the contents in CMS\';\r\nprint \'<br><br>\';\r\n$result=db_query(\"SELECT COUNT(node.nid) AS nid,\r\n   workflow_states.state AS workflow_states_state\r\n FROM node node LEFT JOIN workflow_node workflow_node ON node.nid = workflow_node.nid LEFT JOIN workflow_states workflow_states ON workflow_node.sid = workflow_states.sid\r\n WHERE ((node.type in (\'block\', \'faq\', \'imageflow\', \'page\', \'rotating_panel\',\'country_data\')) AND (workflow_node.sid in (16,17,18,22,19))) GROUP BY workflow_states_state ORDER BY workflow_states_state ASC\");\r\nprint \'<table>\';\r\n print \'<thead>\';\r\n   print \' <tr>\';\r\n    print \'<th>\';\r\n	print \'CMS Workflow State\';\r\n	print \'</th>\';\r\nwhile ($field = db_fetch_object($result))\r\n{\r\n         print \'<th>\';\r\n	print $field->workflow_states_state;\r\n}\r\n  print \'</th>\';         \r\n        \r\nprint \'</tr>\';\r\n  print \'</thead>\';\r\n print \'<tbody>\';\r\n    print \'<tr>\';\r\n	print \'<td>\';\r\n print \'Count\';\r\nprint \'</td>\';\r\n$result=db_query(\"SELECT COUNT(node.nid) AS nid,\r\n   workflow_states.state AS workflow_states_state\r\n FROM node node LEFT JOIN workflow_node workflow_node ON node.nid = workflow_node.nid LEFT JOIN workflow_states workflow_states ON workflow_node.sid = workflow_states.sid\r\n WHERE ((node.type in (\'block\', \'faq\', \'imageflow\', \'page\', \'rotating_panel\',\'country_data\')) AND (workflow_node.sid in (16,17,18,22,19))) GROUP BY workflow_states_state ORDER BY workflow_states_state ASC\");\r\nwhile ($field = db_fetch_object($result))\r\n{\r\n         print \'<td>\';\r\n	print $field->nid;\r\n}\r\n print \'</td>\'; \r\nprint \'</tr>\';\r\nprint \'</tbody>\';\r\nprint \'</table>\';\r\n?>','CMS Admin Workflow Summary',3),(38,'<?php\r\nprint \'Below is a summary and listing of Website Related Feedback in VRM\';\r\nprint \'<br><br>\';\r\n$result=db_query(\"SELECT COUNT(node.nid) AS nid,workflow_states.state AS workflow_states_state FROM node node LEFT JOIN workflow_node workflow_node ON node.nid = workflow_node.nid\r\n INNER JOIN content_field_category node_data_field_category ON node.vid = node_data_field_category.vid LEFT JOIN workflow_states workflow_states ON workflow_node.sid =workflow_states.sid\r\n WHERE (node.type in (\'feedback\')) AND (workflow_node.sid in (26, 27, 28, 29, 30, 31, 32, 33, 34)) AND (node_data_field_category.field_category_value = \'11\') GROUP BY workflow_states_state\r\n  ORDER BY workflow_states_state ASC\");\r\nprint \'<table>\';\r\n print \'<thead>\';\r\n   print \' <tr>\';\r\n    print \'<th>\';\r\n	print \'VRM Workflow State\';\r\n	print \'</th>\';\r\nwhile ($field = db_fetch_object($result))\r\n{\r\n         print \'<th>\';\r\n	print $field->workflow_states_state;\r\n}\r\n  print \'</th>\';         \r\n        \r\nprint \'</tr>\';\r\n  print \'</thead>\';\r\n print \'<tbody>\';\r\n    print \'<tr>\';\r\n	print \'<td>\';\r\n print \'Count\';\r\nprint \'</td>\';\r\n$result=db_query(\"SELECT COUNT(node.nid) AS nid,workflow_states.state AS workflow_states_state FROM node node LEFT JOIN workflow_node workflow_node ON node.nid = workflow_node.nid\r\n INNER JOIN content_field_category node_data_field_category ON node.vid = node_data_field_category.vid LEFT JOIN workflow_states workflow_states ON workflow_node.sid =workflow_states.sid\r\n WHERE (node.type in (\'feedback\')) AND (workflow_node.sid in (26, 27, 28, 29, 30, 31, 32, 33, 34)) AND (node_data_field_category.field_category_value = \'11\') GROUP BY workflow_states_state\r\n  ORDER BY workflow_states_state ASC\");\r\nwhile ($field = db_fetch_object($result))\r\n{\r\n         print \'<td>\';\r\n	print $field->nid;\r\n}\r\n print \'</td>\'; \r\nprint \'</tr>\';\r\nprint \'</tbody>\';\r\nprint \'</table>\';\r\n?>','VRM Feedback Summary',3);
